import { useState } from "react";

const Button = ({ onClick, text }) => (
  <button
    onClick={() => {
      onClick();
    }}
  >
    {text}
  </button>
);

const Header = ({ text }) => <h1>{text}</h1>;

const Output = ({ text, val }) => {
  if (text === "positive") {
    return (
      <div>
        {text} {val} %
      </div>
    );
  }

  return (
    <div>
      {text} {val}
    </div>
  );
};

const Statistics = ({ good, neutral, bad }) => {
  const getAll = () => good + neutral + bad;

  const getAverage = () => {
    let average = 0;
    let count = getAll();

    if (count !== 0) {
      average += good;
      average -= bad;
      average = average / count;
    }
    return average;
  };

  const getPositive = () => {
    let count = getAll();

    if (count !== 0) {
      return good / count;
    }

    return 0;
  };

  const getContent = () => {
    if (good !== 0 || neutral !== 0 || bad !== 0) {
      return (
        <>
          <Output text="good" val={good} />
          <Output text="neutral" val={neutral} />
          <Output text="bad" val={bad} />
          <Output text="all" val={getAll()} />
          <Output text="average" val={getAverage()} />
          <Output text="positive" val={getPositive()} />
        </>
      );
    }

    return <div>No feedback given</div>;
  };

  return (
    <>
      <Header text="statistics" />
      {getContent()}
    </>
  );
};

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);

  const handleClickOnGood = () => setGood(good + 1);

  const handleClickOnNeutral = () => setNeutral(neutral + 1);

  const handleClickOnBad = () => setBad(bad + 1);

  return (
    <>
      <Header text="give feedback" />
      <Button onClick={() => handleClickOnGood()} text="good" />
      <Button
        onClick={() => {
          handleClickOnNeutral();
        }}
        text="neutral"
      />
      <Button
        onClick={() => {
          handleClickOnBad();
        }}
        text="bad"
      />
      <Statistics good={good} neutral={neutral} bad={bad} />
    </>
  );
};

export default App;
