import { useState } from "react";

const Button = ({ onClick, text }) => (
  <button
    onClick={() => {
      onClick();
    }}
  >
    {text}
  </button>
);

const Header = ({ text }) => <h1>{text}</h1>;

const Output = ({ text, val }) => (
  <div>
    {text} {val}
  </div>
);

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);

  const handleClickOnGood = () => setGood(good + 1);

  const handleClickOnNeutral = () => setNeutral(neutral + 1);

  const handleClickOnBad = () => setBad(bad + 1);

  const getAll = () => good + neutral + bad;

  return (
    <>
      <Header text="give feedback" />
      <Button onClick={() => handleClickOnGood()} text="good" />
      <Button
        onClick={() => {
          handleClickOnNeutral();
        }}
        text="bad"
      />
      <Button
        onClick={() => {
          handleClickOnBad();
        }}
        text="neutral"
      />
      <Header text="statistics" />
      <Output text="good" val={good} />
      <Output text="neutral" val={neutral} />
      <Output text="bad" val={bad} />
      <Output text="all" val={getAll()} />
    </>
  );
};

export default App;
